import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import axios from "axios";

// Validation schema for form fields
const schema = yup.object().shape({
    first_name: yup.string().required("First Name is required"),
    last_name: yup.string().required("Last Name is required"),
    employee_id: yup.string().max(10, "Employee ID must be at most 10 characters").required("Employee ID is required"),
    email: yup.string().email("Invalid email").required("Email is required"),
    phone_number: yup.string().length(10, "Phone number must be exactly 10 digits").required("Phone number is required"),
    department: yup.string().required("Department is required"),
    date_of_joining: yup.date().max(new Date(), "Date cannot be in the future").required("Date of joining is required"),
    role: yup.string().required("Role is required")
});

function App() {
    const { register, handleSubmit, formState: { errors }, reset } = useForm({
        resolver: yupResolver(schema)
    });

    // State to store fetched employee data
    const [employees, setEmployees] = useState([]);

    useEffect(() => {
        async function fetchEmployees() {
            try {
                const response = await axios.get("http://localhost:5000/api/employees");
                setEmployees(response.data);
            } catch (error) {
                console.error("Error fetching employees", error);
            }
        }
        fetchEmployees();
    }, [employees]); // Fetch whenever the employee list updates

    // Handle submit (combine first and last name before submitting)
    const onSubmit = async (data) => {
        // Combine first_name and last_name to create full name
        const fullName = `${data.first_name} ${data.last_name}`;

        const newData = { ...data, name: fullName }; // Adding name field as combined full name

        try {
            // Make API call to add employee
            const response = await axios.post("http://localhost:5000/api/employees", newData);
            alert(response.data.message);
            reset(); // Reset form fields
        } catch (error) {
            alert(error.response?.data?.message || "Error");
        }
    };

    // Reset the form (clears out the input values)
    const handleReset = () => {
        reset(); // Clears all form input fields
    };

    return (
        <div className="App">
            <h1>Employee Management System</h1>

            <h2>Add Employee</h2>
            <form onSubmit={handleSubmit(onSubmit)}>
                <div>
                    <input
                        type="text"
                        placeholder="First Name"
                        {...register("first_name")}
                    />
                    <p>{errors.first_name?.message}</p>
                </div>

                <div>
                    <input
                        type="text"
                        placeholder="Last Name"
                        {...register("last_name")}
                    />
                    <p>{errors.last_name?.message}</p>
                </div>

                <div>
                    <input
                        type="text"
                        placeholder="Employee ID"
                        {...register("employee_id")}
                    />
                    <p>{errors.employee_id?.message}</p>
                </div>

                <div>
                    <input
                        type="email"
                        placeholder="Email"
                        {...register("email")}
                    />
                    <p>{errors.email?.message}</p>
                </div>

                <div>
                    <input
                        type="text"
                        placeholder="Phone Number"
                        {...register("phone_number")}
                    />
                    <p>{errors.phone_number?.message}</p>
                </div>

                <div>
                    <select {...register("department")}>
                        <option value="">Select Department</option>
                        <option value="HR">HR</option>
                        <option value="Engineering">Engineering</option>
                        <option value="Marketing">Marketing</option>
                    </select>
                    <p>{errors.department?.message}</p>
                </div>

                <div>
                    <input
                        type="date"
                        {...register("date_of_joining")}
                    />
                    <p>{errors.date_of_joining?.message}</p>
                </div>

                <div>
                    <input
                        type="text"
                        placeholder="Role"
                        {...register("role")}
                    />
                    <p>{errors.role?.message}</p>
                </div>

                {/* Reset Button */}
                <button type="button" onClick={handleReset}>Reset</button>

                {/* Submit Button */}
                <button type="submit">Submit</button>
            </form>

            <h2>Employee List</h2>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Employee ID</th>
                        <th>Email</th>
                        <th>Phone Number</th>
                        <th>Department</th>
                        <th>Role</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((emp) => (
                        <tr key={emp.id}>
                            <td>{emp.name}</td>
                            <td>{emp.employee_id}</td>
                            <td>{emp.email}</td>
                            <td>{emp.phone_number}</td>
                            <td>{emp.department}</td>
                            <td>{emp.role}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default App;
