import React, { useState, useEffect } from "react";
import axios from "axios";

function EmployeeList() {
  const [employees, setEmployees] = useState([]);
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    employee_id: "",
    email: "",
    phone_number: "",
    department: "",
    role: "",
    date_of_joining: "",
  });
  
  const [errors, setErrors] = useState({
    first_name: "",
    last_name: "",
    role: "",
  });

  const [password, setPassword] = useState("");
  const [passwordCorrect, setPasswordCorrect] = useState(false);
  const [error, setError] = useState("");

  const validPassword = "1234"; // Correct password

  // Fetch employee data when password is correct
  useEffect(() => {
    if (passwordCorrect) {
      async function fetchEmployees() {
        try {
          const response = await axios.get("http://localhost:5000/api/employees");
          setEmployees(response.data);
        } catch (error) {
          console.error("Error fetching employees", error);
        }
      }
      fetchEmployees();
    }
  }, [passwordCorrect]);

  const handlePasswordSubmit = () => {
    if (password === validPassword) {
      setPasswordCorrect(true);
      setError("");
    } else {
      setError("Incorrect password!");
    }
  };

  // Form change handler
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    
    // Update form data
    setFormData({ ...formData, [name]: value });
    
    // Validation for first name, last name, and role: only letters are allowed
    if (name === "first_name" || name === "last_name") {
      if (!/^[a-zA-Z]+$/.test(value)) {
        setErrors({ ...errors, [name]: "Only letters are allowed." });
      } else {
        setErrors({ ...errors, [name]: "" });
      }
    }

    // Validate role: only letters are allowed
    if (name === "role") {
      if (!/^[a-zA-Z]+$/.test(value)) {
        setErrors({ ...errors, role: "Only letters are allowed in role." });
      } else {
        setErrors({ ...errors, role: "" });
      }
    }
  };

  // Handle the form submit (add employee)
  const handleFormSubmit = async (e) => {
    e.preventDefault();

    // Before submitting, ensure there are no validation errors
    if (errors.first_name || errors.last_name || errors.role) {
      alert("Please correct the errors in the form before submitting.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/employees", formData);
      console.log(response);
      // Reset form fields after submission
      setFormData({
        first_name: "",
        last_name: "",
        employee_id: "",
        email: "",
        phone_number: "",
        department: "",
        role: "",
        date_of_joining: "",
      });
      setEmployees([...employees, response.data]); // Add the newly added employee to the list
    } catch (error) {
      console.error("Error adding employee", error);
    }
  };

  return (
    <div>
      {passwordCorrect ? (
        <>
          <h2>Employee List</h2>
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Employee ID</th>
                <th>Email</th>
                <th>Phone Number</th>
                <th>Department</th>
                <th>Role</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {employees.map((emp) => (
                <tr key={emp.id}>
                  <td>{emp.name}</td>
                  <td>{emp.employee_id}</td>
                  <td>{emp.email}</td>
                  <td>{emp.phone_number}</td>
                  <td>{emp.department}</td>
                  <td>{emp.role}</td>
                  <td>
                    <button onClick={() => handleEdit(emp)}>Edit</button>
                    <button onClick={() => handleDelete(emp.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <h3>Add Employee</h3>
          <form onSubmit={handleFormSubmit}>
            <div>
              <input
                type="text"
                name="first_name"
                value={formData.first_name}
                onChange={handleInputChange}
                placeholder="First Name"
              />
              {errors.first_name && <span style={{ color: "red" }}>{errors.first_name}</span>}
            </div>
            <div>
              <input
                type="text"
                name="last_name"
                value={formData.last_name}
                onChange={handleInputChange}
                placeholder="Last Name"
              />
              {errors.last_name && <span style={{ color: "red" }}>{errors.last_name}</span>}
            </div>
            <div>
              <input
                type="text"
                name="employee_id"
                value={formData.employee_id}
                onChange={handleInputChange}
                placeholder="Employee ID"
              />
            </div>
            <div>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="Email"
              />
            </div>
            <div>
              <input
                type="text"
                name="phone_number"
                value={formData.phone_number}
                onChange={handleInputChange}
                placeholder="Phone Number"
              />
            </div>
            <div>
              <input
                type="text"
                name="department"
                value={formData.department}
                onChange={handleInputChange}
                placeholder="Department"
              />
            </div>
            <div>
              <input
                type="text"
                name="role"
                value={formData.role}
                onChange={handleInputChange}
                placeholder="Role"
              />
              {errors.role && <span style={{ color: "red" }}>{errors.role}</span>}
            </div>
            <div>
              <input
                type="date"
                name="date_of_joining"
                value={formData.date_of_joining}
                onChange={handleInputChange}
                placeholder="Date of Joining"
              />
            </div>
            <button type="submit">Add Employee</button>
          </form>
        </>
      ) : (
        <div>
          <h2>Enter Password to Access Employee List</h2>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter Password"
          />
          <button onClick={handlePasswordSubmit}>Submit</button>
          {error && <p style={{ color: "red" }}>{error}</p>}
        </div>
      )}
    </div>
  );
}

export default EmployeeList;
